INSERT INTO department (department_id, main_info, name) VALUES (1000, 'Студенти ФГН мають змогу отримати ґрунтовну освіту і сформувати навички науково-дослідної, науково-організаційної та професійно-педагогічної діяльності.
INSERT INTO department (department_id, main_info, name) VALUES (1001, 'Створений у серпні 2000 року на основі Департаменту економічних наук НаУКМА, Факультет економічних наук готує фахівців-економістів на програмах освітньо-кваліфікаційних рівнів бакалавра і спеціаліста, а також має аспірантуру. Освітньо-професійні програми підготовки економістів відповідають державним стандартам вищої освіти України, а також сучасним західним стандартам вищої економічної освіти.', 'Факультет економічних наук');
INSERT INTO department (department_id, main_info, name) VALUES (1002, 'До складу Факультету інформатики НаУКМА входить чотири кафедри: інформатики, математики, мережних технологій, мультимедійних систем, а також лабораторія інформатики.
INSERT INTO department (department_id, main_info, name) VALUES (1003, 'Головне завдання факультету правничих наук – підготовка правників нового покоління, спроможних на високому професійному рівні реалізовувати здобуті знання в усіх сферах багатогранної юридичної науки і практики, які є свідомими, чесними, небайдужими, творчими особистостями, здатними незалежно мислити і відповідально діяти згідно з принципами добра та справедливості, для розвитку відкритого і демократичного суспільства.', 'Факультет правничих наук');
INSERT INTO department (department_id, main_info, name) VALUES (1004, 'Випускники мають можливість продовжити навчання в аспірантурі, докторантурі.', 'Факультет природничих наук');
INSERT INTO department (department_id, main_info, name) VALUES (1005, 'Факультет провадить навчання на двох рівнях вищої освіти — бакалаврат і маґістеріум. До бакалаврату належать такі напрями: політологія (порівняльна політологія), соціальна робота, соціологія, психологія. Маґістрів Факультет соціальних наук і соціальних технологій навчає за такими програмами: політологія, соціологія, соціальна робота, менеджмент в охороні здоров`я, журналістика, зв`язки з громадськістю.', 'Факультет соціальних наук та соціальних технологій');